import { Book, CalendarCheck2, MapPin, Users } from "lucide-react";

function EventCard({ event, userEmail, onJoin }) {
  const hasJoined = event.joined?.includes(userEmail);
  const combinedDateTime = new Date(`${event.date}T${event.time}`);

  return (
    <div className="card bg-base-100 w-94 shadow-sm">
      <figure>
        <img
          className="w-full h-56 object-cover rounded-t-lg"
          src={
            event.imageUrl ||
            "data:image/png;base64,"
          }
          alt={event.title}
        />
      </figure>

      <div className="card-body">
        <h2 className="card-title">{event.title}</h2>
        <p className="text-base">
          {event.description.length > 100
            ? event.description.slice(0, 100) + "..."
            : event.description}
        </p>
        <ul className="my-2 flex flex-col gap-2 text-xs">
          <li className="flex items-center gap-2">
            <CalendarCheck2 size={18} />
            <span className="text-base">
              {combinedDateTime.toLocaleDateString()} at{" "}
              {combinedDateTime.toLocaleTimeString([], {
                hour: "2-digit",
                minute: "2-digit",
              })}
            </span>
          </li>
          <li className="flex items-center gap-2">
            <MapPin size={18} />
            <span className="text-base">{event.location}</span>
          </li>

          <li className="flex items-center gap-2">
            <Book size={18} />
            <span className="text-base">{event.creator}</span>
          </li>
        </ul>
        <div className="flex items-center absolute top-5 right-5 gap-2 badge badge-soft badge-accent">
          <Users size={18} />
          <span className="text-base">{event.joined.length} attendees</span>
        </div>
        <div className="card-actions justify-end">
          <button onClick={onJoin} disabled={hasJoined} className={`btn btn-primary btn-block ${hasJoined ? "btn-disabled":"btn-active"}`} tabIndex="-1" role="button" aria-disabled="true">{hasJoined ? "Joined" : "Join"}</button>
        </div>
      </div>
    </div>
  );
}

export default EventCard;
